-- ----------------------------
-- Table structure for ex_article
-- ----------------------------
DROP TABLE IF EXISTS `ex_article`;
CREATE TABLE `ex_article`  (
  `article_id` int(11) NOT NULL AUTO_INCREMENT,
  `article_type` int(11) NULL DEFAULT NULL,
  `article_time` int(11) NULL DEFAULT NULL,
  `article_title` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL,
  `article_content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL,
  `article_token` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `article_lang` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '',
  `article_plate` int(11) NULL DEFAULT 0 COMMENT '投放平台，0为PC，1为移动端，只对滚动图有效',
  `article_status` int(11) NULL DEFAULT NULL,
  PRIMARY KEY (`article_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 245 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = COMPACT;

