-- ----------------------------
-- Table structure for ex_asset
-- ----------------------------
DROP TABLE IF EXISTS `ex_asset`;
CREATE TABLE `ex_asset`  (
  `asset_id` int(11) NOT NULL AUTO_INCREMENT,
  `asset_plate` int(11) NULL DEFAULT NULL,
  `asset_user` int(11) NULL DEFAULT NULL,
  `asset_coin` int(11) NULL DEFAULT NULL,
  `asset_total` decimal(40, 8) NULL DEFAULT NULL,
  `asset_active` decimal(40, 8) NULL DEFAULT NULL,
  `asset_frozen` decimal(40, 8) NULL DEFAULT NULL,
  `asset_status` int(11) NULL DEFAULT NULL,
  PRIMARY KEY (`asset_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 884 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = COMPACT;

-- ----------------------------
-- Records of ex_asset
-- ----------------------------

-- ----------------------------
-- Table structure for ex_asset_log
-- ----------------------------
DROP TABLE IF EXISTS `ex_asset_log`;
CREATE TABLE `ex_asset_log`  (
  `asset_log_id` int(11) NOT NULL AUTO_INCREMENT,
  `asset_log_plate` int(11) NULL DEFAULT 1,
  `asset_log_user` int(11) NULL DEFAULT 0,
  `asset_log_coin` int(11) NULL DEFAULT 0,
  `asset_log_action` int(11) NULL DEFAULT NULL,
  `asset_log_time` int(11) NULL DEFAULT NULL,
  `asset_log_amount` decimal(40, 8) NULL DEFAULT NULL,
  `asset_log_remark` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL,
  `asset_log_status` int(11) NULL DEFAULT NULL,
  PRIMARY KEY (`asset_log_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 21073 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = COMPACT;

-- ----------------------------
-- Records of ex_asset_log
-- ----------------------------

-- ----------------------------
-- Table structure for ex_coin
-- ----------------------------
DROP TABLE IF EXISTS `ex_coin`;
CREATE TABLE `ex_coin`  (
  `coin_id` int(11) NOT NULL AUTO_INCREMENT,
  `coin_name` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `coin_symbol` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `coin_info` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `coin_icon` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `coin_decimal` int(11) NULL DEFAULT NULL,
  `coin_token_status` int(11) NULL DEFAULT NULL,
  `coin_token_address` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `coin_recharge_status` int(11) NULL DEFAULT NULL,
  `coin_recharge_min_amount` decimal(40, 8) NULL DEFAULT NULL,
  `coin_recharge_static_address_status` int(11) NULL DEFAULT NULL,
  `coin_recharge_static_address` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `coin_withdraw_status` int(11) NULL DEFAULT NULL,
  `coin_withdraw_amount` decimal(40, 8) NULL DEFAULT NULL,
  `coin_withdraw_fee` decimal(40, 8) NULL DEFAULT NULL,
  `coin_index` int(11) NULL DEFAULT NULL,
  `coin_chain` int(11) NULL DEFAULT 0,
  `coin_contract` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '',
  `coin_memo` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '',
  `coin_otc` int(11) NULL DEFAULT NULL,
  `coin_otc_index` int(11) NULL DEFAULT 0,
  `coin_usd` decimal(40, 2) NULL DEFAULT 0.00,
  `coin_status` int(11) NULL DEFAULT NULL,
  PRIMARY KEY (`coin_id`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 25 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of ex_coin
-- ----------------------------
INSERT INTO `ex_coin` VALUES (1, 'BTC', 'BTC', '比特幣是目前使用最爲廣泛的一種數字貨幣。它誕生於2009年1月3日，是一種點對點（P2P）傳輸的數字加密貨幣，總量2100萬枚。比特幣網絡每10分鐘釋放出一定數量幣，預計在2140年達到極限。比特幣漲幅曾接近460萬倍，被投資者稱爲“數字黃金”。截止目前比特幣總市值突破3748億人民幣。比特幣因去中心化、全球流通、低交易費用、匿名流通等特點，備受科技愛好者青睞。近來華爾街、多國央行等傳統金融機構開始研究比特幣區塊鏈技術，中國人民銀行也公佈研發數字貨幣的計劃。日本政府正式承認比特幣爲法定支付方式，越來越多的日本商家接受了比特幣支付。特點 比特幣因去中心化、全球流通、低交易費用 、匿名流通、專屬所有權、跨平臺挖掘等特點，備受科技愛好者青睞。近來華爾街、多國央行等傳統金融機構開始研究比特幣區塊鏈技術，日本政府正式承認比特幣爲法定支付方式，越來越多的日本商家接受了比特幣支付。', '/attachment/images/20200613/6a186352ac6566982ec2e387001b1f78.png', 8, 0, '', 1, 0.00010000, 1, '0x8c317314bcAa72cd9bafDa38B2F476041dF9ba67', 1, 0.00100000, 0.00100000, 99, 1, '0', '', 0, 0, 15516.65, 1);
